import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image, FlatList, ScrollView } from 'react-native';
import { AntDesign, Ionicons, MaterialCommunityIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';

const DATA = [
    {
        id: '1',
        img: 'https://canifa.com/img/800/1000/resize/9/h/9ha21a020-sb982-0000-1-ghep.jpg',
        title1: 'SALE 20%',
    },
    {
        id: '2',
        img: 'https://canifa.com/img/486/733/resize/8/t/8tw21c004-fg084-l-1.jpg',
        title1: 'SALE 50%',
    },
    {
        id: '3',
        img: 'https://canifa.com/img/800/1000/resize/8/b/8bp21w005-sa200-l-1.jpg',
        title1: 'SALE 30%',
    },
    {
        id: '4',
        img: 'https://canifa.com/img/800/1000/resize/8/b/8bj20w002-sj608-1.jpg',
        title1: 'SALE UP TO 70%',
    },
];

const Item = (props) => (


    <View style={styles.test}>

        <View style={styles.test1}>
            <Image
                style={styles.tinyLogo}
                source={{
                    uri: props.img,
                }}
            />

        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#ffff' }}>
            <Text style={styles.txt}>{props.title1}</Text>
            <Text style={styles.txt1}>{props.id}</Text>
        </View>

    </View>

);

export default function App({ navigation }) {
    const renderItem = ({ item }) => (
        <Item title1={item.title1} img={item.img} />

    );
    return (
        <View style={styles.container}>

            <View style={styles.header}>
                <TouchableOpacity
                 onPress={() => navigation.goBack()}
                >
                    <View style={styles.item}>
                        <AntDesign name="left" size={24} color="black" />
                    </View>
                </TouchableOpacity>

                <TouchableOpacity>
                    <Ionicons name="md-search" size={24} color="black"/>
                </TouchableOpacity>

                <View style={styles.text}>
                    <Text style={styles.date}> NAM</Text>
                </View>
                <TouchableOpacity>
                    <View style={styles.item}>
                        <Ionicons name="md-heart-outline" size={24} color="black" />
                    </View>
                </TouchableOpacity>

                <TouchableOpacity>
                    <AntDesign name="shoppingcart" size={24} color="black" />
                </TouchableOpacity>

            </View>

            <View style={styles.main}>
                <View style={styles.title}>
                    <Text style={styles.Nam}>10 sản phẩm</Text>
                </View>
                <View style={styles.option}>
                    <TouchableOpacity>
                        <View style={styles.icon}>
                            <Text style={styles.op}>Bộ lọc</Text>
                            <View style={styles.arrow}>
                                <MaterialCommunityIcons name="arrow-down-drop-circle-outline" size={24} color="black" />
                            </View>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity>
                        <View style={styles.icon}>
                            <Text style={styles.op}>Sắp xếp</Text>
                            <View style={styles.arrow}>
                                <MaterialCommunityIcons name="arrow-down-drop-circle-outline" size={24} color="black" />
                            </View>
                        </View>
                    </TouchableOpacity>
                </View>
            </View>
            <View style={{ width: 500, height: 1, marginTop: 10, backgroundColor: '#DDDDDD' }} />
            <ScrollView>
                <FlatList
                    horizontal={true}
                    data={DATA}
                    renderItem={renderItem}
                    keyExtractor={item => item.id}
                />
                <View style={styles.product}>
                    <ScrollView horizontal={true} style={styles.scrollview}>
                        <View>
                            <TouchableOpacity>
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N1.jpg')}
                                />
                                <Text style={styles.name}>Áo nỉ nam vai raglan</Text>
                                <Text style={styles.price}>249.000 ₫</Text>
                            </TouchableOpacity>

                        </View>
                        <View>
                            <TouchableOpacity>
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N2.jpg')}

                                />
                                <Text style={styles.name}>Áo nỉ nam in hình Mickey</Text>
                                <Text style={styles.price}>249.000 ₫</Text>
                            </TouchableOpacity>

                        </View>
                    </ScrollView>
                    <ScrollView horizontal={true} style={styles.scrollview}>
                        <View>
                            <TouchableOpacity>
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N3.jpg')}
                                />
                                <Text style={styles.name}>Áo nỉ nam</Text>
                                <Text style={styles.price}>649.000 ₫</Text>
                            </TouchableOpacity>

                        </View>
                        <View>
                            <TouchableOpacity
                                onPress={() => navigation.navigate('detail')}
                            >
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N1.jpg')}

                                />
                                <Text style={styles.name}>Áo nỉ có mũ nam regular</Text>
                                <Text style={styles.price}>299.000 ₫</Text>
                            </TouchableOpacity>

                        </View>
                    </ScrollView>
                    <ScrollView horizontal={true} style={styles.scrollview}>
                        <View>
                            <TouchableOpacity>
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N5.jpg')}
                                />
                                <Text style={styles.name}>Áo nỉ nam</Text>
                                <Text style={styles.price}>499.000 ₫</Text>
                            </TouchableOpacity>
                        </View>
                        <View>
                            <TouchableOpacity>
                                <Image
                                    style={styles.sp}
                                    source={require('../image/N6.jpg')}
                                />
                                <Text style={styles.name}>Áo nỉ unisex người lớn</Text>
                                <Text style={styles.price}>599.000 ₫</Text>
                            </TouchableOpacity>

                        </View>
                    </ScrollView>
                    <View>
                        <TouchableOpacity>
                            <Image
                                style={{ width: 170, height: 250, marginRight: 200 }}
                                source={require('../image/N1.jpg')}
                            />
                            <Text style={styles.name}>Áo nỉ nam vai raglan</Text>
                            <Text style={styles.price}>249.000 ₫</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ScrollView>

        </View>
    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        paddingTop: 15,
        // justifyContent: 'center',
        // alignItems: 'center',
    },
    header: {
        flexDirection: 'row',
        margin: 5,
        alignItems: 'center',

    },
    text: {
        marginLeft: 90,
        marginRight: 100,

    },
    date: {
        color: 'black',
        fontSize: 25,
        fontWeight: 'bold',
        marginLeft: 5,
    },
    item: {
        marginRight: 6,
    },
    main: {
        flexDirection: 'row',
        marginTop: 5,
    },
    option: {
        flexDirection: 'row',
    },
    icon: {
        flexDirection: 'row',
        marginLeft:10,
    },
    title: {
        marginLeft: 20,
        marginRight: 130,
    },
    txt: {
        marginLeft: 35,
        color: 'red',
        fontWeight: 'bold',
    },
    Nam: {
        fontSize: 15,
        fontWeight: 'bold',
    },
    op: {
        margin: 5,
        // marginLeft: 6,
    },
    arrow: {
        marginTop: 3,
    },
    tinyLogo: {
        width: 150,
        height: 150,
        margin: 10,
    },
    scrollview: {
        marginBottom: 15,
    },
    sp: {
        width: 170,
        height: 250,
        margin: 15,
    },
    product: {
        // margin: 10,
        alignItems: 'center',
    },
    name: {
        marginLeft: 18,
        fontSize: 15,
        fontWeight: 'bold',
    },
    price: {
        marginLeft: 18,
        color: 'red',
        fontWeight: 'bold',
    }
});
