import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const DATA1 = [
  {
    id: '1',
    title: 'Wechat 1',
    subTitle: 'WeChat không chỉ là một ...',
    
  },
  {
    id: '2',
    title: 'Zalo 1',
    subTitle: 'Sub Item s',
    
  },
  {
    id: '3',
    title: 'Viber 1',
    subTitle: 'Sub Item s',
   
  },
];
const Item = ({ item , navigation, itemId}) => (
  <TouchableOpacity onPress={() => navigation.navigate('Detail',{
    itemId: item.id
  })}
  >
    <View style={styles.item}>
      {/* cột thứ 1 */}
      <View>
      <Image
          style={styles.tinyLogo}
          source={item.urlimage}
        />
      </View>
      {/* cột thứ 2 */}
      <View>
        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.subTitle}>{item.subTitle}</Text>
      </View>
    </View>
  </TouchableOpacity>    
);

const Category = ({route}) => { /*nhận param qua route*/
    const navigation = useNavigation();
    const {itemId} = route.params;
    const renderItem = ({ item }) => (
    <Item item={item} navigation = {navigation} itemId={itemId}/>
  );

        let DATA = [];
        if(itemId == '1')
        {
            DATA = DATA1
        }

  return (
    <SafeAreaView style={styles.container}>
      <FlatList        
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
    </SafeAreaView>
  );
}

