import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';
<NavigationContainer>screenOptions={{ headerShown: false }}in</NavigationContainer>
const DATA = [
  {
    id: '1',
    alo:'https://truongquochoc.com/wp-content/uploads/2020/10/45-hinh-nen-xe-oto-dep-nhat-the-gioi-rat-nhieu-hang-4.jpg',
    title: 'First Item',
  },
  {
    id: '2',
    alo:'https://truongquochoc.com/wp-content/uploads/2020/10/45-hinh-nen-xe-oto-dep-nhat-the-gioi-rat-nhieu-hang-5.jpg',
    title: 'Second Item',
  },
  {
    id: '3',
    alo:'https://truongquochoc.com/wp-content/uploads/2020/10/45-hinh-nen-xe-oto-dep-nhat-the-gioi-rat-nhieu-hang-6.jpg',
    title: 'Third Item',
  },
  {
    id: '4',
    alo:'https://truongquochoc.com/wp-content/uploads/2020/10/45-hinh-nen-xe-oto-dep-nhat-the-gioi-rat-nhieu-hang-7.jpg',
    title: 'Third Item',
  },
];

const Item =(props)=>(


  <View style={styles.test}>
    
  
    <View style={{marginBottom:3,backgroundColor:'#ffff'}}>
    <Text style={styles.txt}>{props.title}</Text>
    <Text style={styles.txt1}>{props.id}</Text>
    
    </View>
    <View style={styles.test1}>
    <Image
          style={styles.tinyLogo}
          source={{
            uri: props.alo,
          }}
        />
     
    </View>
  </View>
  
  );

export default function trangchu({ navigation }) {
  const renderItem = ({ item }) => (
    <Item title={item.title}  alo ={item.alo} />
    
  );
    return (
        <View atyle={styles.container}>
            <Text style={styles.text}> Trang Chá»§ </Text>
            <View style={styles.thanhtren}>
                <View style={styles.iconmess}>
                    <MaterialCommunityIcons name="email-outline" size={24} color="black" />
                    <View style={styles.seach}>
                        <AntDesign name="search1" size={24} color="black" />
                    </View>
                </View>
                <View style={styles.logocanifa}>     
                <Image
                        style={styles.logoha}
                        source={require("../image/logocanifa.jpg")}
                    />        
                </View>
                <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
                    <View style={styles.bag}>
                        <SimpleLineIcons name="bag" size={24} color="black" />
                    </View>
                </View>
            </View>

        <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />

        </View>
    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 5,
        fontSize: 24,
        fontWeight: 'bold',
        color: '#635555',
        marginLeft: 5,
    },
    logoha: {
        width: 120,
        height: 70,
    },
    thanhtren: {
        flexDirection: 'row',
        marginTop: 5,
        marginLeft: 10,
    },
    iconmess: {
        flexDirection: 'row',
        marginTop: 23,
    },
    heart: {
        flexDirection: 'row',
        marginTop: 23,
        marginLeft: 60,
    },
    seach: {
        marginLeft: 10,
        marginRight: 56,
    },
    bag: {
        marginLeft: 10,
    },
    tinyLogo: {
      width: 100,
      height: 100,
    },
  

});
