import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';
import { FontAwesome } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
export default function trangchu({ navigation }) {
    return (
        <View atyle={styles.container}>


            <Text style={styles.text}> Danh Mục Sản Phẩm</Text>
            <ScrollView>
            <View style={styles.hang1}>

                <Image
                    style={styles.hinhanh5}
                    source={require("./img/hinh1.jpg")}
                />
                <Text style={styles.textchung}>Áo body + quần jean nữ hot 2021</Text>

            </View>

            <View><Text style={styles.textgia}>275.000đ</Text></View>

            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>

                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 230, height: 1, marginTop: 30, marginLeft: -70, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang2}>
                <Image
                    style={styles.hinhanh5}
                    source={require("./img/hinh3.jpg")}
                />
                <Text style={styles.textchung}>Áo sơ mi nam kẻ caro hot 2021 </Text>
            </View>
            <View><Text style={styles.textgia}>200.000đ</Text></View>
            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>

                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 230, height: 1, marginTop: 30, marginLeft: -70, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("./img/hinh2.jpg")}
                />
                <Text style={styles.textchung}>Áo thun free size ống tay lỡ nữ, chất bền + đẹp</Text>
            </View>
            <View><Text style={styles.textgia}>150.000đ</Text></View>
            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>
                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("./img/hinh4.jpg")}
                />
                <Text style={styles.textchung}>Áo thun free size ống tay lỡ + chân váy kẻ caro</Text>
            </View>
            <View><Text style={styles.textgia}>350.000đ</Text></View>
            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>
                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("./img/hinh5.jpg")}
                />
                <Text style={styles.textchung}>Áo khoác kaki nam hot 2021</Text>
            </View>
            <View><Text style={styles.textgia}>250.000đ</Text></View>
            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>
                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            </View>

</ScrollView>
            <View style={styles.bottom}>
                <View style={styles.bottom2}>
                    <AntDesign name="home" size={25} color="black" />
                </View>
                <View style={styles.bottom1}>
                    <AntDesign name="search1" size={25} color="black" />
                </View>
                <View style={styles.bottom1}>
                    <AntDesign name="shoppingcart" size={25} color="black" />
                </View>
                <View style={styles.bottom1}>
                    <AntDesign name="filetext1" size={25} color="black" />
                </View>
                <View style={styles.bottom1}>
                    <AntDesign name="qrcode" size={25} color="black" />
                </View>
            </View>

            

        </View>
    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 10,
        fontSize: 24,
        fontWeight: 'bold',
        color: '#635555',
        marginLeft: 5,
    },

    bottom: {
        width: 400,
        backgroundColor: 'white',
        height: 60,
        flexDirection: 'row',
        marginTop: -90,
    },
    icon3: {
        marginTop: -150,
        marginLeft: 120,
    },
    bottom1: {
        marginLeft: 55,
        marginTop: 5,
    },
    bottom2: {
        marginLeft: 15,
        marginTop: 5,
    },
    chubottom: {
        flexDirection: 'row',
        marginTop: -30,
    },
    chubottom1: {
        marginLeft: 1,
        marginTop: 2,
    },
    chubottom2: {
        marginLeft: 20,
        marginTop: 2,
    },
    chubottom3: {
        marginLeft: 27,
        marginTop: 2,
    },
    chubottom4: {
        marginLeft: 25,
        marginTop: 2,
    },
    chubottom5: {
        marginLeft: 33,
        marginTop: 2,
    },
    hinhanh5: {
        width: 100,
        height: 100,
        borderRadius: 50,
    },
    hang1: {
        flexDirection: 'row',
        marginTop: 10,
        width: 200,
    },
    hang2: {
        flexDirection: 'row',
        marginTop: 15,
        width: 200,
    },
    hang3: {
        flexDirection: 'row',
        marginTop: 15,
        width: 200,
    },
    textchung: {
        marginTop: 25,
        marginLeft: 25,
        fontSize: 15,
    },
    textgia: {
        color: 'red',
        fontSize: 16,
        marginLeft: 130,
        marginTop: -35,
        fontWeight: 'bold',
    },
    iconxin: {
        flexDirection: 'row',
        marginLeft: 130,
        marginTop: -10,
    },
    cachicon: {
        paddingRight: 20,
    },

    button: {
        width: 155,
        height: 40,
        backgroundColor: '#DE4547',
        borderRadius: 45,
        marginTop: -20,
        marginLeft: 15,
    },
    icon3: {
        marginLeft: 125,
        marginTop: -29,
    },
});
