import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const DATA1 = [
  {
    id: '1',
    title: 'Wechat 1',
    subTitle: 'WeChat không chỉ là một ...',
    urlimage: require('./images/bongda.png'),
  },
  {
    id: '2',
    title: 'Zalo 1',
    subTitle: 'Sub Item s',
    urlimage: require('./images/bongda.png'),
  },
  {
    id: '3',
    title: 'Viber 1',
    subTitle: 'Sub Item s',
    urlimage: require('./images/bongda.png'),
  },
];
const Item = ({ item , navigation, itemId}) => (
  <TouchableOpacity onPress={() => navigation.navigate('Detail',{
    itemId: item.id
  })}
  >
    <View style={styles.item}>
      {/* cột thứ 1 */}
      <View>
      <Image
          style={styles.tinyLogo}
          source={item.urlimage}
        />
      </View>
      {/* cột thứ 2 */}
      <View>
        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.subTitle}>{item.subTitle}</Text>
      </View>
    </View>
  </TouchableOpacity>    
);

const Category = ({route}) => { /*nhận param qua route*/
    const navigation = useNavigation();
    const {itemId} = route.params;
    const renderItem = ({ item }) => (
    <Item item={item} navigation = {navigation} itemId={itemId}/>
  );

        let DATA = [];
        if(itemId == '1')
        {
            DATA = DATA1
        }else if(itemId == '2'){
            DATA = DATA2
        }else if(itemId == '3'){
            DATA = DATA3
        }

  return (
    <SafeAreaView style={styles.container}>
      <FlatList        
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
    </SafeAreaView>
  );
}

export default function trangchu({ navigation }) {
    return (
        <View atyle={styles.container}>
            <Text style={styles.text}> Trang Chủ </Text>
            <View style={styles.thanhtren}>
                <View style={styles.iconmess}>
                    <MaterialCommunityIcons name="email-outline" size={24} color="black" />
                    <View style={styles.seach}>
                        <AntDesign name="search1" size={24} color="black" />
                    </View>
                </View>
                <View style={styles.logocanifa}>
                    <Image
                        style={styles.logoha}
                        source={require("../image/logocanifa.jpg")}
                    />
                </View>
                <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
                    <View style={styles.bag}>
                        <SimpleLineIcons name="bag" size={24} color="black" />
                    </View>
                </View>
            </View>
            

        </View>
    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 5,
        fontSize: 24,
        fontWeight: 'bold',
        color: '#635555',
        marginLeft: 5,
    },
    logoha: {
        width: 120,
        height: 70,
    },
    thanhtren: {
        flexDirection: 'row',
        marginTop: 5,
        marginLeft: 10,
    },
    iconmess: {
        flexDirection: 'row',
        marginTop: 23,
    },
    heart: {
        flexDirection: 'row',
        marginTop: 23,
        marginLeft: 60,
    },
    seach: {
        marginLeft: 10,
        marginRight: 56,
    },
    bag: {
        marginLeft: 10,
    },

});
