import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';


const DATA1 = [
    {
      id: '1',
      title: 'Wechat 1',
      subTitle: 'WeChat không chỉ là một ...',
      urlimage: ('./images/bongda.png'),
    },
    {
      id: '2',
      title: 'Zalo 1',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),
    },
    {
      id: '3',
      title: 'Viber 1',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),
    },
  ];

const DATA2 = [
    {
      id: '1',
      title: 'Wechat 2',
      subTitle: 'WeChat không chỉ là một ...',
      urlimage: require('./images/bongda.png'),
    },
    {
      id: '2',
      title: 'Zalo 2',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),
    },
    {
      id: '3',
      title: 'Viber 2',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),  
    },
  ];
  const DATA3 = [
    {
      id: '1',
      title: 'Wechat 3',
      subTitle: 'WeChat không chỉ là một ...',
      urlimage: require('./images/bongda.png'),
    },
    {
      id: '2',
      title: 'Zalo 3',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),
    },
    {
      id: '3',
      title: 'Viber 3',
      subTitle: 'Sub Item s',
      urlimage: require('./images/bongda.png'),
    },
  ];
const Item = ({ item , navigation, itemId}) => (
  <TouchableOpacity onPress={() => navigation.navigate('Detail',{
    itemId: item.id
  })}
  >
    <View style={styles.item}>
      {/* cột thứ 1 */}
      <View>
      <Image
          style={styles.tinyLogo}
          source={item.urlimage}
        />
      </View>
      {/* cột thứ 2 */}
      <View>
        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.subTitle}>{item.subTitle}</Text>
      </View>
    </View>
  </TouchableOpacity>    
);

const Category = ({route}) => { /*nhận param qua route*/
    const navigation = useNavigation();
    const {itemId} = route.params;
    const renderItem = ({ item }) => (
    <Item item={item} navigation = {navigation} itemId={itemId}/>
  );

        let DATA = [];
        if(itemId == '1')
        {
            DATA = DATA1
        }else if(itemId == '2'){
            DATA = DATA2
        }else if(itemId == '3'){
            DATA = DATA3
        }

  return (
    <SafeAreaView style={styles.container}>
      <FlatList        
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: 'red',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
    flexDirection: "row",
  },
  title: {
    fontSize: 32,
  },
  subTitle: {
    fontSize: 16,
  },
  tinyLogo:{
    height: 64,
    width: 64,
  }
});

export default Category;

