import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';

const DATA = [
    {
        id: '1',
        anh: 'https://canifa.com/img/800/1000/resize/8/t/8tw21w007-sg344-l-1.jpg',
        title: 'Áo Hoodie Unisex Form Rộng',
        subtitle: 'Mã SP:6BP21003',
        gia: '499.000đ',
        mausac: 'Màu sắc:',
        kichco: 'Kích cỡ:'
    },

];

const Item = (props) => (


    <View style={styles.flastlit}>

        <View style={styles.anhfl}>
            <Image
                style={styles.tinyLogo}
                source={{
                    uri: props.anh,
                }}
            />
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 80, }}>
            <Text style={styles.txt1}>{props.id}</Text>
            <Text style={styles.chuduoi}>{props.title}</Text>
            <Text style={styles.masp}>{props.subtitle}</Text>
            <Text style={styles.gia}>{props.gia}</Text>

        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 120, }}>
            <Text style={styles.mausac}>{props.mausac}</Text>
            <View style={styles.anhmau}>
                <Image
                    style={styles.ha}
                    source={require("../image/anhmau1.jpg")}
                />
                <View style={styles.kichco}>
                    <Text style={styles.kichco}>{props.kichco}</Text>
                    <View style={styles.cacsize}>
                        <View style={{ width: 30, height: 30, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                    </View>
                </View>
                <View style={styles.chusize}>
                    <Text>S</Text>
                    <Text>M</Text>
                    <Text>L</Text>
                    <Text>XL</Text>
                </View>
            </View>

        </View>

    </View>

);

export default function chitietsanpham({ navigation }) {
    const renderItem = ({ item }) => (
        <Item title={item.title} anh={item.anh} subtitle={item.subtitle} gia={item.gia} mausac={item.mausac} kichco={item.kichco} />




    );
    return (

        <View atyle={styles.container}>

            <Text style={styles.text}> Chi Tiết Sản Phẩm </Text>
            <View style={styles.thanhtren}>
                <View style={styles.iconmess}>
                    <MaterialCommunityIcons name="email-outline" size={24} color="black" />
                    <View style={styles.seach}>
                        <AntDesign name="search1" size={24} color="black" />
                    </View>
                </View>
                <View style={styles.logocanifa}>
                    <Image
                        style={styles.logoha}
                        source={require("../image/logocanifa.jpg")}
                    />
                </View>
                <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
                    <View style={styles.bag}>
                        <SimpleLineIcons name="bag" size={24} color="black" />
                    </View>
                </View>
            </View>

            <FlatList
                data={DATA}
                renderItem={renderItem}
                keyExtractor={item => item.id}
            />

            <TouchableOpacity>
                <View style={styles.bottom}>
                    <View style={styles.bottom2}>
                        <AntDesign name="home" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="search1" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="shoppingcart" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="filetext1" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="qrcode" size={25} color="black" />
                    </View>
                </View>

                <View style={styles.chubottom}>
                    <View style={styles.chubottom1}>
                        <Text>Trang Chủ</Text>
                    </View>
                    <View style={styles.chubottom2}>
                        <Text>Tìm Kiếm</Text>
                    </View>
                    <View style={styles.chubottom3}>
                        <Text>Giỏ Hàng</Text>
                    </View>
                    <View style={styles.chubottom4}>
                        <Text>Hồ Sơ</Text>
                    </View>
                    <View style={styles.chubottom5}>
                        <Text>Cấu Hình</Text>
                    </View>
                </View>

            </TouchableOpacity>
        </View>

    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 30,
        fontSize: 24,
        fontWeight: 'bold',
        color: '#635555',
        marginLeft: 5,
    },
    logoha: {
        width: 90,
        height: 50,
        marginLeft: 20,
    },
    thanhtren: {
        flexDirection: 'row',
        marginTop: 5,
        marginLeft: 10,
    },
    iconmess: {
        flexDirection: 'row',
        marginTop: 23,
    },
    heart: {
        flexDirection: 'row',
        marginTop: 23,
        marginLeft: 60,
    },
    seach: {
        marginLeft: 10,
        marginRight: 56,
    },
    bag: {
        marginLeft: 10,
    },
    tinyLogo: {
        width: 350,
        height: 350,
        marginLeft: 3,
    },
    anhfl: {
        marginTop: 10,
        marginLeft: 10,
    },
    chuduoi: {
        marginLeft: 20,
        fontWeight: 'bold',
    },

    bottom1: {
        marginLeft: 55,
        marginTop: 5,
    },
    bottom2: {
        marginLeft: 15,
        marginTop: 5,
    },
    chubottom: {
        flexDirection: 'row',
        marginTop: -30,
    },
    chubottom1: {
        marginLeft: 1,
        marginTop: 2,
    },
    chubottom2: {
        marginLeft: 20,
        marginTop: 2,
    },
    chubottom3: {
        marginLeft: 27,
        marginTop: 2,
    },
    chubottom4: {
        marginLeft: 25,
        marginTop: 2,
    },
    chubottom5: {
        marginLeft: 33,
        marginTop: 2,
    },

    bottom: {
        width: 400,
        backgroundColor: 'white',
        height: 60,
        flexDirection: 'row',
        marginTop: 9,
    },
    masp: {
        color: '#E6CFCF',
        marginLeft: 20,
        marginTop: 3,
    },
    gia: {
        color: 'red',
        fontWeight: 'bold',
        marginLeft: 20,
        marginTop: 3,
    },
    mausac: {
        color: '#ECBFBF',
        marginLeft: 20,
        marginTop: 3,
    },
    kichco: {
        color: '#ECBFBF',
        marginLeft: 10,
        marginTop: 3,
    },
    ha: {
        width: 30,
        height: 30,
        borderRadius: 50,
        marginLeft: 30,
        marginTop: 5,
    },
    cacsize: {
        flexDirection: 'row',
        marginLeft: 20,
    },
    chusize: {

    },


});
