import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const DATA = [
    {
      id: '1',
      title: 'Wechat',
      subTitle: 'WeChat không chỉ là một ...',
      urlimage: require('./image/logocanifa.jpg'),
    },
    {
      id: '2',
      title: 'Giới thiệu phần mềm zalo',
      subTitle: 'Sub Item s',
      urlimage: require('./images/phapluat.jpg'),
    },
    {
      id: '3',
      title: 'Giới thiệu phần mềm Viber',
      subTitle: 'Sub Item s',
      urlimage: require('./images/xahoi.png'),    
    },
  ];
  const Item = ({item, navigation }) => (
    <TouchableOpacity onPress={() => navigation.navigate('Category',{itemId: item.id})}
    >
      <View style={styles.item}>
        {/* cột thứ 1 */}
        <View>
        <Image
            style={styles.tinyLogo}
            source={item.urlimage}
          />
        </View>
        {/* cột thứ 2 */}
        <View style= {styles.rangeimg}>
          <Text style={styles.title}>{item.title}</Text>
          <Text style={styles.subTitle}>{item.subTitle}</Text>
        </View>
      </View>
    </TouchableOpacity>    
  );
  const Home = () => {
    const navigation = useNavigation();
    const renderItem = ({item }) => (
      <Item item={item} navigation={navigation}/>  /* render navigation*/
    );
  
    return (
      <SafeAreaView style={styles.container}>
        <FlatList
          data={DATA}
          renderItem={renderItem}
          keyExtractor={item => item.id}
        
        />
      </SafeAreaView>
    );
  }  
export default function trangchu({ navigation }) {
    return (
        <View atyle={styles.container}>
            <Text style={styles.text}> Trang Chủ </Text>
            <View style={styles.thanhtren}>
                <View style={styles.iconmess}>
                    <MaterialCommunityIcons name="email-outline" size={24} color="black" />
                    <View style={styles.seach}>
                        <AntDesign name="search1" size={24} color="black" />
                    </View>
                </View>
                <View style={styles.logocanifa}>
                    <Image
                        style={styles.logoha}
                        source={require("./image/logocanifa.jpg")}
                    />
                </View>
                <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
                    <View style={styles.bag}>
                        <SimpleLineIcons name="bag" size={24} color="black" />
                    </View>
                </View>
            </View>
            

        </View>
    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 5,
        fontSize: 24,
        fontWeight: 'bold',
        color: '#635555',
        marginLeft: 5,
    },
    logoha: {
        width: 120,
        height: 70,
    },
    thanhtren: {
        flexDirection: 'row',
        marginTop: 5,
        marginLeft: 10,
    },
    iconmess: {
        flexDirection: 'row',
        marginTop: 23,
    },
    heart: {
        flexDirection: 'row',
        marginTop: 23,
        marginLeft: 60,
    },
    seach: {
        marginLeft: 10,
        marginRight: 56,
    },
    bag: {
        marginLeft: 10,
    },

});
