import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';


const DATA = [
    {
        id: '1',
        anh: 'https://canifa.com/img/800/1000/resize/8/t/8tw21w007-sg344-l-1.jpg',
        title: 'Áo Hoodie Unisex Form Rộng',
        subtitle: 'Mã SP:6BP21003',
        gia: '499.000đ',
        mausac: 'Màu sắc:',
        kichco: 'Kích cỡ:',
        mota: 'Mô tả:',
        chatlieu: 'Chất liệu:',
        huongdansd: 'Hướng dẫn sử dụng:',
    },

];

const Item = (props) => (


    <View style={styles.flastlit}>

        <View style={styles.anhfl}>
            <Image
                style={styles.tinyLogo}
                source={{
                    uri: props.anh,
                }}
            />
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 80, }}>
            <Text style={styles.txt1}>{props.id}</Text>
            <Text style={styles.chuduoi}>{props.title}</Text>
            <Text style={styles.masp}>{props.subtitle}</Text>
            <Text style={styles.gia}>{props.gia}</Text>

        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 120, }}>
            <Text style={styles.mausac}>{props.mausac}</Text>
            <View style={styles.anhmau}>
                <Image
                    style={styles.ha}
                    source={require("../image/anhmau1.jpg")}
                />
                <View style={styles.kichco}>
                    <Text style={styles.kichco}>{props.kichco}</Text>
                    <View style={styles.cacsize}>
                        <View style={{ width: 30, height: 30, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                        <View style={{ width: 30, height: 30, marginLeft: 5, backgroundColor: 'skyblue' }} />
                    </View>
                </View>
                <View style={styles.chusize}>
                    <Text style={styles.S}>S</Text>
                    <Text style={styles.M}>M</Text>
                    <Text style={styles.L}>L</Text>
                    <Text style={styles.XL}>XL</Text>
                </View>
            </View>
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 80, }}>
            <Text style={styles.mota}>{props.mota}</Text>
            <Text style={styles.mota}>
                Áo hoodie nam sợi cotton acylic dệt phối màu, họa tiết trám chìm thân trước, thân sau và tay áo dáng regular, cổ lọ, tay dài.
            </Text>
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 50, }}>
            <Text style={styles.mota}>{props.chatlieu}</Text>
            <Text style={styles.mota}>
                42% cotton, 42% acrylic, 16% nylon
            </Text>
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 100, }}>
            <Text style={styles.mota}>{props.huongdansd}</Text>
            <Text style={styles.mota}>
                Giặt máy ở chế độ nhẹ, nhiệt độ thường. Không sử dụng chất tẩy. Phơi trong bóng mát. Sấy khô ở nhiệt độ thấp. Là ở nhiệt độ trung bình 150 độ c. Giặt với sản phẩm cùng màu. Không là lên chi tiết trang trí.
            </Text>
        </View>
        <View style={{ marginBottom: 3, backgroundColor: '#fff', height: 110, }}>
            <View style={styles.oto}>
                <MaterialCommunityIcons name="truck-delivery-outline" size={24} color="skyblue" />
                <Text style={styles.vanchuyen}>Miễn phí vận chuyển cho đơn hàng từ 499.000đ</Text>
            </View>
            <View style={{ width: 250, height: 1, marginLeft: 20,marginTop:15, backgroundColor: '#FCB7B7' }} />
            <View style={styles.hoandoi}>
                <AntDesign name="retweet" size={24} color="skyblue" />
                <Text style={styles.doihang}>Miễn phí đổi hàng trong 30 ngày kể từ ngày mua.</Text>
            </View>

        </View>
        <View style={styles.button}>
                    <Button
                        title="Thanh Toán"
                        color="#ffff"
                        onPress={() => navigation.navigate('dathangtc')}
                    />
                    <View style={styles.icon3}>
                        <AntDesign name="rightcircleo" size={24} color="white" />
                    </View>
                </View>
        <View style={{ width: 400, height: 170, backgroundColor: '#fff' }} />
    </View>

);

export default function chitietsanpham({ navigation }) {
    const renderItem = ({ item }) => (
        <Item title={item.title} anh={item.anh} subtitle={item.subtitle} gia={item.gia} mausac={item.mausac} kichco={item.kichco} mota={item.mota}
            chatlieu={item.chatlieu} huongdansd={item.huongdansd} />

    );
    return (

        <View atyle={styles.container}>

            <Text style={styles.text}> Chi Tiết Sản Phẩm </Text>
            <View style={styles.thanhtren}>
                <View style={styles.iconmess}>
                    <MaterialCommunityIcons name="email-outline" size={24} color="black" />
                    <View style={styles.seach}>
                        <AntDesign name="search1" size={24} color="black" />
                    </View>
                </View>
                <View style={styles.logocanifa}>
                    <Image
                        style={styles.logoha}
                        source={require("../image/logocanifa.jpg")}
                    />
                </View>
                <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
                    <View style={styles.bag}>
                        <SimpleLineIcons name="bag" size={24} color="black" />
                    </View>
                </View>
            </View>

            <FlatList
                data={DATA}
                renderItem={renderItem}
                keyExtractor={item => item.id}
            />


            <TouchableOpacity>
                <View style={styles.bottom}>
                    <View style={styles.bottom2}>
                        <AntDesign name="home" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="search1" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="shoppingcart" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="filetext1" size={25} color="black" />
                    </View>
                    <View style={styles.bottom1}>
                        <AntDesign name="qrcode" size={25} color="black" />
                    </View>
                </View>

                <View style={styles.chubottom}>
                    <View style={styles.chubottom1}>
                        <Text>Trang Chủ</Text>
                    </View>
                    <View style={styles.chubottom2}>
                        <Text>Tìm Kiếm</Text>
                    </View>
                    <View style={styles.chubottom3}>
                        <Text>Giỏ Hàng</Text>
                    </View>
                    <View style={styles.chubottom4}>
                        <Text>Hồ Sơ</Text>
                    </View>
                    <View style={styles.chubottom5}>
                        <Text>Cấu Hình</Text>
                    </View>
                </View>

            </TouchableOpacity>
        </View>

    );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },

    text: {
        marginTop: 30,
