import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, PointPropType, Image, TouchableOpacity, FlatList, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';

const DATA = [
  {
    id: '1',
    anh: 'https://canifa.com/img/800/1000/resize/8/t/8tw21w007-sg344-l-1.jpg',
    title: '230.000đ',
  },
  {
    id: '2',
    anh: 'https://canifa.com/img/800/1000/resize/6/t/6tw21w037-sk010-s-1.jpg',
    title: '250.000đ',
  },
  {
    id: '3',
    anh: 'https://canifa.com/img/800/1000/resize/5/t/5tw21w005-sy151-l-1.jpg',
    title: '160.000',
  },
  {
    id: '4',
    anh: 'https://canifa.com/img/800/1000/resize/3/t/3tw21w004-sk010-110-1.jpg',
    title: '240.000',
  },
  {
    id: '4',
    anh: 'https://canifa.com/img/800/1000/resize/6/o/6ot21w008-sm378-s-1.jpg',
    title: '490.000đ',
  },
  {
    id: '4',
    anh: 'https://canifa.com/img/800/1000/resize/6/t/6th21w002-se075-s-1.jpg',
    title: '210.000đ',
  },
];

const Item = (props) => (


  <View style={styles.flastlit}>

    <View style={styles.anhfl}>
      <Image
        style={styles.tinyLogo}
        source={{
          uri: props.anh,
        }}
      />
    </View>
    <View style={{ marginBottom: 3, backgroundColor: '#F3D1D1' }}>

      <Text style={styles.txt1}>{props.id}</Text>
      <Text style={styles.chuduoi}>{props.title}</Text>

    </View>

  </View>

);

export default function trangchu({ navigation }) {
  const renderItem = ({ item }) => (
    <Item title={item.title} anh={item.anh} />

  );
  return (

    <View atyle={styles.container}>

      <Text style={styles.text}> Trang Chủ </Text>
      <View style={styles.thanhtren}>
        <View style={styles.iconmess}>
          <MaterialCommunityIcons name="email-outline" size={24} color="black" />
          <View style={styles.seach}>
            <AntDesign name="search1" size={24} color="black" />
          </View>
        </View>
        <View style={styles.logocanifa}>
          <Image
            style={styles.logoha}
            source={require("../image/logocanifa.jpg")}
          />
        </View>
        <View style={styles.heart}><AntDesign name="hearto" size={24} color="black" />
          <View style={styles.bag}>
            <SimpleLineIcons name="bag" size={24} color="black" />
          </View>
        </View>
      </View>

      <FlatList
        horizontal={true}
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
      <ScrollView>
            <View style={styles.hang1}>

                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh1.jpg")}
                />
                <Text style={styles.textchung}>Áo body + quần jean nữ hot 2021</Text>

            </View>

            <View><Text style={styles.textgia}>275.000đ</Text></View>

            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>

                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 230, height: 1, marginTop: 30, marginLeft: -70, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang2}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh3.jpg")}
                />
                <Text style={styles.textchung}>Áo sơ mi nam kẻ caro hot 2021 </Text>
            </View>
            <View><Text style={styles.textgia}>200.000đ</Text></View>
            <View style={styles.iconxin}>
                <View style={styles.cachicon}>
                    <AntDesign name="pluscircleo" size={20} color="black" />
                </View>

                <AntDesign name="minuscircleo" size={20} color="black" />
                <View style={{ width: 230, height: 1, marginTop: 30, marginLeft: -70, backgroundColor: 'skyblue' }} />
            </View>
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh2.jpg")}
                />
                <Text style={styles.textchung}>Áo thun free size ống tay lỡ nữ, chất bền + đẹp</Text>
            </View>
            <View><Text style={styles.textgia}>150.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
           
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh4.jpg")}
                />
                <Text style={styles.textchung}>Áo thun free size ống tay lỡ + chân váy kẻ caro</Text>
            </View>
            <View><Text style={styles.textgia}>350.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh5.jpg")}
                />
                <Text style={styles.textchung}>Áo khoác kaki nam hot 2021</Text>
            </View>
            <View><Text style={styles.textgia}>250.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
           
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh5.jpg")}
                />
                <Text style={styles.textchung}>Áo khoác kaki nam hot 2021</Text>
            </View>
            <View><Text style={styles.textgia}>250.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh5.jpg")}
                />
                <Text style={styles.textchung}>Áo khoác kaki nam hot 2021</Text>
            </View>
            <View><Text style={styles.textgia}>250.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            
            <View style={styles.hang3}>
                <Image
                    style={styles.hinhanh5}
                    source={require("../image/hinh5.jpg")}
                />
                <Text style={styles.textchung}>Áo khoác kaki nam hot 2021</Text>
            </View>
            <View><Text style={styles.textgia}>250.000đ</Text></View>
            
                <View style={{ width: 390, height: 1, marginTop: 30, marginLeft: -200, backgroundColor: 'skyblue' }} />
            

</ScrollView>
    </View>

  );
}
const Stack = createNativeStackNavigator();

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },

  text: {
    marginTop: 30,
    fontSize: 24,
    fontWeight: 'bold',
    color: '#635555',
    marginLeft: 5,
  },
  logoha: {
    width: 90,
    height: 50,
    marginLeft: 25,
  },
  thanhtren: {
    flexDirection: 'row',
    marginTop: 5,
    marginLeft: 10,
  },
  iconmess: {
    flexDirection: 'row',
    marginTop: 23,
  },
  heart: {
    flexDirection: 'row',
    marginTop: 23,
    marginLeft: 60,
  },
  seach: {
    marginLeft: 10,
    marginRight: 56,
  },
  bag: {
    marginLeft: 10,
  },
  tinyLogo: {
    width: 100,
    height: 100,
  },
  anhfl: {
    marginTop: 10,
    marginLeft: 10,
  },
  chuduoi: {
    marginLeft: 34,
  },
  hang1: {
    flexDirection: 'row',
    marginTop: 10,
    width: 200,
},
hang2: {
    flexDirection: 'row',
    marginTop: 15,
    width: 200,
},
hang3: {
    flexDirection: 'row',
    marginTop: 15,
    width: 200,
},
textchung: {
    marginTop: 25,
    marginLeft: 25,
    fontSize: 15,
},
textgia: {
    color: 'red',
    fontSize: 16,
    marginLeft: 130,
    marginTop: -35,
    fontWeight: 'bold',
},
iconxin: {
    flexDirection: 'row',
    marginLeft: 130,
    marginTop: -10,
},
cachicon: {
    paddingRight: 20,
},
hinhanh5: {
  width: 100,
  height: 100,
  borderRadius: 50,
},


});
